    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(rb1.isSelected())
        {
            Double a = sl1.getValue() * 1.8 +32;
            tf1.setText(a.toString());
        }
        
        if(rb2.isSelected())
        {
            Double a = (sl1.getValue() - 32 ) / 1.8;
            tf1.setText(a.toString());
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        if(cb1.getSelectedItem() == "Kilometri")
        {
            try{
            Double a = Double.parseDouble(rtf1.getText()) * 0.621371;
            rtf2.setText(a.toString());
            }catch(Exception e){}
        }
        
        if(cb1.getSelectedItem() == "Milje")
        {
            try{
            Double a = Double.parseDouble(rtf1.getText()) / 0.621371;
            rtf2.setText(a.toString());
            }catch(Exception e) {}
        }
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void cb1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cb1ActionPerformed
        if(cb1.getSelectedIndex() == 0)
            cb2.setSelectedIndex(0);
        else
            cb2.setSelectedIndex(1);
    }//GEN-LAST:event_cb1ActionPerformed

    private void cb2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cb2ActionPerformed
        if(cb2.getSelectedIndex() == 0)
            cb1.setSelectedIndex(0);
        else
            cb1.setSelectedIndex(1);
    }//GEN-LAST:event_cb2ActionPerformed

    private void rb1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rb1ActionPerformed
        sl1.setMinimum(-50);
        sl1.setMaximum(50);
        sl1.setValue(0);
    }//GEN-LAST:event_rb1ActionPerformed

    private void rb2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rb2ActionPerformed
        sl1.setMinimum(-58);
        sl1.setMaximum(122);
        sl1.setValue(32);
    }//GEN-LAST:event_rb2ActionPerformed

